import React from "react";
import "../CheckoutCartItem/style.css";
import { useStoreContext } from "../../utils/GlobalState";
import { REMOVE_FROM_CART, UPDATE_CART_QUANTITY } from "../../utils/actions";
import { idbPromise } from "../../utils/helpers";

const CheckoutCartItem = ({ item }) => {
	const [, dispatch] = useStoreContext();

	const removeFromCart = (item) => {
		dispatch({
			type: REMOVE_FROM_CART,
			_id: item._id,
		});
		idbPromise("cart", "delete", { ...item });
	};

	const onChange = (e) => {
		const value = e.target.value;

		if (value === "0") {
			dispatch({
				type: REMOVE_FROM_CART,
				_id: item._id,
			});

			idbPromise("cart", "delete", { ...item });
		} else {
			dispatch({
				type: UPDATE_CART_QUANTITY,
				_id: item._id,
				purchaseQuantity: parseInt(value),
			});

			idbPromise("cart", "put", { ...item, purchaseQuantity: parseInt(value) });
		}
	};

	return (
		// <div className="flex-row">
		//     <div>
		//         <div>Date: Input from Calendar component </div>
		//         <img
		//             src={`/images/${item.image}`}
		//             alt=""
		//         />
		//     </div>
		//     <div>
		//         <div>{item.name}, ${item.price}</div>
		//         <div>
		//             <span>Qty:</span>
		//             <input
		//                 type="number"
		//                 placeholder="1"
		//                 value={item.purchaseQuantity}
		//                 onChange={onChange}
		//             />
		//             <span
		//                 role="img"
		//                 aria-label="trash"
		//                 onClick={() => removeFromCart(item)}
		//             >
		//                 üóëÔ∏è
		//             </span>
		//         </div>
		//     </div>
		// </div>
		<div>
			<div class="container ">
				<div class="row">
					<div class="col cart-card">
						<div class="d-flex justify-content-between date">
						<div>{new Date(item.date).toDateString()}</div>
						</div>
						<div class="d-flex justify-content-between items">
							<div className="item">{item.name}</div>
							<p className="item">${item.price}</p>
						</div>
							<div >
								<span>Qty:</span>
								<input
									id="quantity"
									type="number"
									placeholder="1"
									value={item.purchaseQuantity}
									onChange={onChange}
								/>
									{" "}
								<span
									role="img"
									aria-label="trash"
									onClick={() => removeFromCart(item)}
								>
									üóëÔ∏è
								</span>
							</div>


					</div>
					{/* <div class=" cart-price">Add Order Notes</div>
					<div >Subtotal</div>
					<div >Delivery Fees</div> */}
				</div>
			</div>
		</div>
	);
};

export default CheckoutCartItem;
